{
  "version": "0.2",
  "phases": {
    "pre_build": {
      "commands": [
        "npm install -g aws-cdk@1.84.0  && python -m pip install -r \"requirements.txt\" && pip3 install bandit"
      ]
    },
    "build": {
      "commands": [
        "cdk synth && bridgecrew -f cdk.out/CdkPipelineStack.template.json --skip-check CKV_AWS_7,CKV_AWS_18,CKV_AWS_21,CKV_AWS_111 && bandit -r lambda/",
        "npx cdk synth"
      ]
    }
  },
  "artifacts": {
    "base-directory": "cdk.out",
    "files": "**/*"
  }
}
